import React, { useState } from "react";
import { IoIosRefreshCircle } from "react-icons/io";

const kindergartens = [
  { week: "Понедельник", time: "18:30", name: "Сказочный домик", address: "Пушкинская ул, дом 3", color: "#1F3F4D" },
  { week: "Вторник", time: "09:00", name: "Веселый муравейник", address: "Ленина пр, дом 15", color: "#FF5733" },
  { week: "Среда", time: "14:00", name: "Солнечный лучик", address: "Гагарина ул, дом 22", color: "#33FF57" },
  { week: "Четверг", time: "10:30", name: "Лукоморье", address: "Мира ул, дом 5", color: "#3357FF" },
  { week: "Пятница", time: "16:00", name: "Теремок", address: "Садовая ул, дом 12", color: "#FF33A6" },
  { week: "Суббота", time: "11:00", name: "Радуга", address: "Космонавтов пр, дом 8", color: "#A633FF" },
  { week: "Воскресенье", time: "17:00", name: "Сказка", address: "Победы пр, дом 10", color: "#1F3F4D" },
  { week: "Понедельник", time: "09:00", name: "Маленький гений", address: "Лермонтова ул, дом 2", color: "#FF5733" },
  { week: "Вторник", time: "14:00", name: "Аистенок", address: "Советская ул, дом 18", color: "#33FF57" },
  { week: "Среда", time: "10:30", name: "Колосок", address: "Московская ул, дом 7", color: "#3357FF" },
  { week: "Четверг", time: "16:00", name: "Светлячок", address: "Петровская ул, дом 9", color: "#FF33A6" },
  { week: "Пятница", time: "11:00", name: "Золотая рыбка", address: "Набережная ул, дом 13", color: "#A633FF" },
  { week: "Суббота", time: "17:00", name: "Вишенка", address: "Парковая ул, дом 4", color: "#1F3F4D" },
  { week: "Воскресенье", time: "18:30", name: "Аленький цветочек", address: "Октябрьская ул, дом 6", color: "#FF5733" },
  { week: "Понедельник", time: "14:00", name: "Подсолнух", address: "Пионерская ул, дом 11", color: "#33FF57" },
  { week: "Вторник", time: "10:30", name: "Ромашка", address: "Строителей ул, дом 14", color: "#3357FF" },
  { week: "Среда", time: "16:00", name: "Кораблик", address: "Школьная ул, дом 16", color: "#FF33A6" },
  { week: "Четверг", time: "11:00", name: "Журавлик", address: "Луговая ул, дом 19", color: "#A633FF" },
  { week: "Пятница", time: "17:00", name: "Березка", address: "Заречная ул, дом 20", color: "#1F3F4D" },
  { week: "Суббота", time: "09:00", name: "Калинка", address: "Клубная ул, дом 23", color: "#FF5733" },
  { week: "Воскресенье", time: "14:00", name: "Ладушка", address: "Цветочная ул, дом 24", color: "#33FF57" },
  { week: "Понедельник", time: "10:30", name: "Чебурашка", address: "Зеленая ул, дом 26", color: "#3357FF" },
  { week: "Вторник", time: "16:00", name: "Топтыжка", address: "Полевая ул, дом 27", color: "#FF33A6" },
  { week: "Среда", time: "11:00", name: "Звездочка", address: "Уютная ул, дом 28", color: "#A633FF" },
  { week: "Четверг", time: "17:00", name: "Ласточка", address: "Солнечная ул, дом 29", color: "#1F3F4D" },
  { week: "Пятница", time: "18:30", name: "Лесная сказка", address: "Речная ул, дом 30", color: "#FF5733" },
  { week: "Суббота", time: "14:00", name: "Василек", address: "Горная ул, дом 31", color: "#33FF57" },
  { week: "Воскресенье", time: "10:30", name: "Жаворонок", address: "Луговая ул, дом 32", color: "#3357FF" },
  { week: "Понедельник", time: "16:00", name: "Росинка", address: "Весенняя ул, дом 33", color: "#FF33A6" },
  { week: "Вторник", time: "11:00", name: "Колокольчик", address: "Осенняя ул, дом 34", color: "#A633FF" }
];

function TableCard({ time, name, address, color }) {
  return (
    <div className="table_card" style={{ borderLeft: `5px solid ${color}` }}>
      <div className="table_card_time">{time}</div>
      <div className="table_card_name">{name}</div>
      <div className="table_card_address">{address}</div>
    </div>
  );
}

export default function Table() {
  const [data, setData] = useState(kindergartens);
  const [collapsed, setCollapsed] = useState({});
  const daysOfWeek = ["Понедельник", "Вторник", "Среда", "Четверг", "Пятница", "Суббота", "Воскресенье"];

  const toggleCollapse = (day) => {
    setCollapsed(prevState => ({
      ...prevState,
      [day]: !prevState[day]
    }));
  };

  const refreshKindergartens = () => {
    // В будущем здесь будет запрос к базе данных для обновления данных
    // Например, fetch('/api/kindergartens').then(response => response.json()).then(data => setData(data));
    // На данный момент просто перезагружаем исходные данные
    setData([...kindergartens]);
  };

  const sortByTime = (a, b) => {
    const timeA = a.time.split(":").map(Number);
    const timeB = b.time.split(":").map(Number);
    return timeA[0] - timeB[0] || timeA[1] - timeB[1];
  };

  return (
    <div className="card_main pd20">
      <div className="textright">
        <IoIosRefreshCircle onClick={refreshKindergartens} className="refresh-icon" />
      </div>
      {daysOfWeek.map((day, index) => (
        <div key={index} className="card_main pd20 mb15">
          <div className="weekday">
            <button onClick={() => toggleCollapse(day)} className="cardopen">
              {collapsed[day] ? "+" : "-"}
            </button>
            {day}
          </div>
          {!collapsed[day] && (
            <div className="grid3 mt20">
              {data
                .filter(kindergarten => kindergarten.week === day)
                .sort(sortByTime)
                .map((kindergarten, idx) => (
                  <TableCard
                    key={idx}
                    time={kindergarten.time}
                    name={kindergarten.name}
                    address={kindergarten.address}
                    color={kindergarten.color}
                  />
                ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
