import React, { useState, useEffect } from "react";
import { IoIosRefreshCircle } from "react-icons/io";
import { MdEdit } from "react-icons/md";
import axios from 'axios';
import AddTable from "./AddTable";

const api = 'http://localhost:3001/api/schedules';

function EditModal({ isOpen, onClose, schedule, onSave }) {
  const [time, setTime] = useState('');
  const [day, setDay] = useState('');

  const daysOfWeek = ["Понедельник", "Вторник", "Среда", "Четверг", "Пятница", "Суббота", "Воскресенье"];

  useEffect(() => {
    if (schedule) {
      setTime(schedule.time);
      setDay(schedule.day_of_week);
    }
  }, [schedule]);

  const handleSave = () => {
    onSave({ ...schedule, time, day_of_week: day });
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal">
      <div className="modal_content">
        <h2>Редактировать</h2>
        <label>
          Время:
          <input type="time" value={time} onChange={(e) => setTime(e.target.value)} />
        </label>
        <br/>
        <label>
          День недели:
          <select value={day} onChange={(e) => setDay(e.target.value)}>
            {daysOfWeek.map((day, idx) => (
              <option key={idx} value={day}>{day}</option>
            ))}
          </select>
        </label>
        <br/>
        <button onClick={handleSave}>Обновить</button>
        <button onClick={onClose}>Отмена</button>
      </div>
    </div>
  );
}

function TableCard({ schedule, onSave }) {
  const [isEditing, setIsEditing] = useState(false);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleClose = () => {
    setIsEditing(false);
  };

  return (
    <div className="table_card" style={{ borderLeft: `5px solid ${schedule.metro_color}` }}>
      <div className="table_card_time">{schedule.time.slice(0, 5)}</div>
      <div className="table_card_name">{schedule.name}</div>
      <div className="table_card_address">{schedule.address}</div>
      <MdEdit className="edit-icon" onClick={handleEdit} />
      <EditModal
        isOpen={isEditing}
        onClose={handleClose}
        schedule={schedule}
        onSave={onSave}
      />
    </div>
  );
}

export default function Table() {
  const [data, setData] = useState([]);
  const [collapsed, setCollapsed] = useState({});

  const daysOfWeek = ["Понедельник", "Вторник", "Среда", "Четверг", "Пятница", "Суббота", "Воскресенье"];

  const toggleCollapse = (day) => {
    setCollapsed(prevState => ({
      ...prevState,
      [day]: !prevState[day]
    }));
  };

  const refreshKindergartens = async () => {
    try {
      const response = await axios.get(api);
      setData(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching schedules:', error);
    }
  };

  useEffect(() => {
    refreshKindergartens();
  }, []);

  const sortByTime = (a, b) => {
    const timeA = a.time.split(":").map(Number);
    const timeB = b.time.split(":").map(Number);
    return timeA[0] - timeB[0] || timeA[1] - timeB[1];
  };

  const handleSave = async (updatedSchedule) => {
    try {
      await axios.put(`${api}/${updatedSchedule.id}`, updatedSchedule);
      setData(data.map(item => item.id === updatedSchedule.id ? updatedSchedule : item));
    } catch (error) {
      console.error('Error updating schedule:', error);
    }
  };
  const handleScheduleAdded = (newSchedule) => {
    setData([...data, newSchedule]);
  };
  return (
    <div className="card_main pd20">
      <div className="textright">
        <IoIosRefreshCircle onClick={refreshKindergartens} className="refresh-icon" />
      </div>
      {daysOfWeek.map((day, index) => (
        <div key={index} className="card_main pd20 mb15">
          <div className="weekday">
            <button onClick={() => toggleCollapse(day)} className="cardopen">
              {collapsed[day] ? "+" : "-"}
            </button>
            {day}
          </div>
          {!collapsed[day] && (
            <div className="grid3 mt20">
              {data
                .filter(schedule => schedule.day_of_week === day)
                .sort(sortByTime)
                .map((schedule, idx) => (
                  <TableCard
                    key={idx}
                    schedule={schedule}
                    onSave={handleSave}
                  />
                ))}
            </div>
          )}
        </div>
      ))}
    <AddTable onScheduleAdded={refreshKindergartens()} />
    </div>
  );
}
