import React, { useState } from 'react';
import { FilialCard } from "./FilialCard";
import { IoIosRefreshCircle } from "react-icons/io";
import axios from 'axios';
const api = 'http://localhost:3001/api/filials'
export default function Filials() {
  const initialKindergartens = [
    {
      name: "Детский сад №1",
      address: "ул. Пушкина, д. 10",
      price: "30,000 руб.",
      metro: "Пушкинская",
      color: "#ff5733"
    },
    {
      name: "Детский сад №2",
      address: "пр. Ленина, д. 15",
      price: "28,000 руб.",
      metro: "Ленинская",
      color: "#33ff57"
    },
    {
      name: "Детский сад №3",
      address: "ул. Гагарина, д. 22",
      price: "32,000 руб.",
      metro: "Гагаринская",
      color: "#3357ff"
    },
    {
      name: "Детский сад №4",
      address: "ул. Мира, д. 5",
      price: "29,000 руб.",
      metro: "Мирная",
      color: "#ff33a6"
    },
    {
      name: "Детский сад №5",
      address: "ул. Садовая, д. 12",
      price: "31,000 руб.",
      metro: "Садовая",
      color: "#a633ff"
    }
  ];

  const [kindergartens, setKindergartens] = useState(initialKindergartens);
  const refreshKindergartens = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/filials');
      setTrialKindergartens(response.data);
    } catch (error) {
      console.error('Error fetching filials:', error);
    }
  };

  return (
    <div className='card_main pd20'>
      <div className='textright'><IoIosRefreshCircle onClick={refreshKindergartens} className="refresh-icon"/></div>
      {kindergartens.map((kindergarten, index) => (
        <FilialCard
          key={index}
          name={kindergarten.name}
          address={kindergarten.address}
          price={kindergarten.price}
          metro={kindergarten.metro}
          color={kindergarten.color}
        />
      ))}
    </div>
  );
}
