import React, { useState } from 'react';
import { FilialCard } from "./FilialCard";

export default function Filials() {
  const [priceRange, setPriceRange] = useState([0, 50000]); // Диапазон цен от 0 до 50,000 рублей

  const kindergartens = [
    {
      name: "Детский сад №1",
      address: "ул. Пушкина, д. 10",
      price: 30000,
      metro: "Пушкинская",
      color: "#ff5733"
    },
    {
      name: "Детский сад №2",
      address: "пр. Ленина, д. 15",
      price: 28000,
      metro: "Ленинская",
      color: "#33ff57"
    },
    {
      name: "Детский сад №3",
      address: "ул. Гагарина, д. 22",
      price: 32000,
      metro: "Гагаринская",
      color: "#3357ff"
    },
    {
      name: "Детский сад №4",
      address: "ул. Мира, д. 5",
      price: 29000,
      metro: "Мирная",
      color: "#ff33a6"
    },
    {
      name: "Детский сад №5",
      address: "ул. Садовая, д. 12",
      price: 31000,
      metro: "Садовая",
      color: "#a633ff"
    }
  ];

  const filteredKindergartens = kindergartens.filter(k => k.price >= priceRange[0] && k.price <= priceRange[1]);

  const handlePriceChange = (event) => {
    const { name, value } = event.target;
    setPriceRange(prevRange => {
      if (name === 'min') {
        return [parseInt(value), prevRange[1]];
      } else {
        return [prevRange[0], parseInt(value)];
      }
    });
  };

  return (
    <div className="container">
      <div className="sidebar">
        <div className="otdel_name">отдел: <span className="highlight">NP1</span></div>
        <div className="sb_buttons">
          <Link to="/"><div className="sb_button sb_active">Филиалы</div></Link>
          <Link to="/"><div className="sb_button sb_noactive">Пробные</div></Link>
          <Link to="/"><div className="sb_button sb_noactive">Расписание</div></Link>
        </div>
        <div className="filter">
          <h3>Фильтр по цене</h3>
          <div>
            <label>Минимальная цена:</label>
            <input
              type="number"
              name="min"
              value={priceRange[0]}
              onChange={handlePriceChange}
            />
          </div>
          <div>
            <label>Максимальная цена:</label>
            <input
              type="number"
              name="max"
              value={priceRange[1]}
              onChange={handlePriceChange}
            />
          </div>
        </div>
      </div>
      <div className="main-content">
        {filteredKindergartens.map((kindergarten, index) => (
          <FilialCard
            key={index}
            name={kindergarten.name}
            address={kindergarten.address}
            price={`${kindergarten.price} руб.`}
            metro={kindergarten.metro}
            color={kindergarten.color}
          />
        ))}
      </div>
    </div>
  );
}
