import React, { useState } from 'react';
import { ProbnFilialCard } from "./ProbnFilialCard"; // Предполагается, что FilialCard также нужно будет изменить для отображения новых полей
import { IoIosRefreshCircle } from "react-icons/io";

export default function ProbnFilials() {
  const initialTrialKindergartens = [
    {
      name: "Детский сад №1",
      address: "ул. Пушкина, д. 10",
      price: "30,000 руб.",
      metro: "Пушкинская",
      color: "#ff5733",
      trialDate: "2024-05-20",
      dayOfWeek: "Понедельник",
      time: "10:00"
    },
    {
      name: "Детский сад №2",
      address: "пр. Ленина, д. 15",
      price: "28,000 руб.",
      metro: "Ленинская",
      color: "#33ff57",
      trialDate: "2024-05-20",
      dayOfWeek: "Понедельник",
      time: "9:00"
    },
    {
      name: "Детский сад №3",
      address: "ул. Гагарина, д. 22",
      price: "32,000 руб.",
      metro: "Гагаринская",
      color: "#3357ff",
      trialDate: "2024-05-22",
      dayOfWeek: "Среда",
      time: "12:00"
    },
    {
      name: "Детский сад №4",
      address: "ул. Мира, д. 5",
      price: "29,000 руб.",
      metro: "Мирная",
      color: "#ff33a6",
      trialDate: "2024-05-23",
      dayOfWeek: "Четверг",
      time: "13:00"
    },
    {
      name: "Детский сад №5",
      address: "ул. Садовая, д. 12",
      price: "31,000 руб.",
      metro: "Садовая",
      color: "#a633ff",
      trialDate: "2024-05-24",
      dayOfWeek: "Пятница",
      time: "14:00"
    }
  ];

  const [trialKindergartens, setTrialKindergartens] = useState(initialTrialKindergartens);

  const refreshTrialKindergartens = () => {
    // В будущем здесь будет запрос к базе данных для получения новых данных.
    // Пока что обновим массив статически.
    const updatedTrialKindergartens = [
      ...initialTrialKindergartens,
      {
        name: "Детский сад №6",
        address: "ул. Новая, д. 3",
        price: "27,000 руб.",
        metro: "Новая",
        color: "#ff5733",
        trialDate: "2024-05-25",
        dayOfWeek: "Суббота",
        time: "15:00"
      }
    ];
    setTrialKindergartens(updatedTrialKindergartens);
  };

  const sortedKindergartens = [...trialKindergartens].sort((a, b) => {
    const dateA = new Date(`${a.trialDate}T${a.time}:00`);
    const dateB = new Date(`${b.trialDate}T${b.time}:00`);
    return dateA - dateB;
  });

  return (
    <div className='card_main pd20'>
      <div className='textright'><IoIosRefreshCircle onClick={refreshTrialKindergartens} className="refresh-icon"/></div>
      {sortedKindergartens.map((kindergarten, index) => (
        <ProbnFilialCard
          key={index}
          name={kindergarten.name}
          address={kindergarten.address}
          price={kindergarten.price}
          metro={kindergarten.metro}
          color={kindergarten.color}
          trialDate={kindergarten.trialDate}
          dayOfWeek={kindergarten.dayOfWeek}
          time={kindergarten.time}
        />
      ))}
    </div>
  );
}
