import React, { useState } from 'react';
import { FilialCard } from "./FilialCard";

export default function Filials() {
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(Infinity);

  const kindergartens = [
    {
      name: "Детский сад №1",
      address: "ул. Пушкина, д. 10",
      price: 30000,
      metro: "Пушкинская",
      color: "#ff5733"
    },
    {
      name: "Детский сад №2",
      address: "пр. Ленина, д. 15",
      price: 28000,
      metro: "Ленинская",
      color: "#33ff57"
    },
    {
      name: "Детский сад №3",
      address: "ул. Гагарина, д. 22",
      price: 32000,
      metro: "Гагаринская",
      color: "#3357ff"
    },
    {
      name: "Детский сад №4",
      address: "ул. Мира, д. 5",
      price: 29000,
      metro: "Мирная",
      color: "#ff33a6"
    },
    {
      name: "Детский сад №5",
      address: "ул. Садовая, д. 12",
      price: 31000,
      metro: "Садовая",
      color: "#a633ff"
    }
  ];

  const handleMinPriceChange = (event) => {
    setMinPrice(event.target.value ? parseInt(event.target.value) : 0);
  };

  const handleMaxPriceChange = (event) => {
    setMaxPrice(event.target.value ? parseInt(event.target.value) : Infinity);
  };

  const filteredKindergartens = kindergartens.filter(kindergarten => 
    kindergarten.price >= minPrice && kindergarten.price <= maxPrice
  );

  return (
    <div>
      <div className='filter'>
        <label>
          Min Price:
          <input type="number" value={minPrice === 0 ? '' : minPrice} onChange={handleMinPriceChange} />
        </label>
        <label>
          Max Price:
          <input type="number" value={maxPrice === Infinity ? '' : maxPrice} onChange={handleMaxPriceChange} />
        </label>
      </div>
      <div className='card_main pd20'>
        {filteredKindergartens.map((kindergarten, index) => (
          <FilialCard
            key={index}
            name={kindergarten.name}
            address={kindergarten.address}
            price={`${kindergarten.price} руб.`}
            metro={kindergarten.metro}
            color={kindergarten.color}
          />
        ))}
      </div>
    </div>
  );
}
