import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Card from './Card';
import Modal from './Modal';

const PayInfo = () => {
    const [invoices, setInvoices] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [filter, setFilter] = useState(''); // Состояние фильтра

    useEffect(() => {
        const fetchInvoices = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/payinfo');
                setInvoices(response.data.items);
            } catch (error) {
                console.error('Error fetching invoices:', error);
            }
        };

        fetchInvoices();
    }, []);

    useEffect(() => {
        setFilter('currentMonth'); // Установка фильтра при монтировании компонента
    }, []);

    const handleSave = async (paymentData) => {
        try {
            const response = await axios.post('http://localhost:3001/api/payinfo', paymentData, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            setInvoices([...invoices, response.data]);
        } catch (error) {
            console.error('Error creating payment:', error);
        }
    };

    const filterInvoices = (invoices) => {
        const currentMonth = new Date().getMonth();
        const currentYear = new Date().getFullYear();

        return invoices.filter(invoice => {
            const invoiceDate = new Date(invoice.created_at);
            const invoiceMonth = invoiceDate.getMonth();
            const invoiceYear = invoiceDate.getFullYear();
            if (filter === 'currentMonth') {
                return invoiceMonth === currentMonth && invoiceYear === currentYear;
            }
            // Добавить другие условия фильтрации здесь, если нужно
            return true;
        });
    };

    return (
        <div>
            <h2>Список счетов</h2>
            <div>
                <label>
                    Фильтр:
                    <select value={filter} onChange={(e) => setFilter(e.target.value)}>
                        <option value="currentMonth">Текущий месяц</option>
                        {/* Добавить другие опции фильтра, если нужно */}
                    </select>
                </label>
            </div>
            <button onClick={() => setIsModalOpen(true)}>Добавить новый платеж</button>
            <div>
                {filterInvoices(invoices).map(invoice => (
                    <Card key={invoice.id} invoice={invoice} />
                ))}
            </div>
            <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} onSave={handleSave} />
        </div>
    );
};

export default PayInfo;
