import React, { useEffect, useState } from "react";
import { IoIosRefreshCircle } from "react-icons/io";
import axios from 'axios';

const api = 'http://localhost:3001/api/schedules';

function TableCard({ time, name, address, color }) {
  return (
    <div className="table_card" style={{ borderLeft: `5px solid ${color}` }}>
      <div className="table_card_time">{time}</div>
      <div className="table_card_name">{name}</div>
      <div className="table_card_address">{address}</div>
    </div>
  );
}

export default function Table() {
  const [data, setData] = useState([]);
  const [collapsed, setCollapsed] = useState({});
  const daysOfWeek = ["Понедельник", "Вторник", "Среда", "Четверг", "Пятница", "Суббота", "Воскресенье"];

  const toggleCollapse = (day) => {
    setCollapsed(prevState => ({
      ...prevState,
      [day]: !prevState[day]
    }));
  };

  const refreshKindergartens = async () => {
    try {
      const response = await axios.get(api);
      setData(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching schedules:', error);
    }
  };

  useEffect(() => {
    refreshKindergartens();
  }, []);

  const sortByTime = (a, b) => {
    const timeA = a.time.split(":").map(Number);
    const timeB = b.time.split(":").map(Number);
    return timeA[0] - timeB[0] || timeA[1] - timeB[1];
  };

  return (
    <div className="card_main pd20">
      <div className="textright">
        <IoIosRefreshCircle onClick={refreshKindergartens} className="refresh-icon" />
      </div>
      {daysOfWeek.map((day, index) => (
        <div key={index} className="card_main pd20 mb15">
          <div className="weekday">
            <button onClick={() => toggleCollapse(day)} className="cardopen">
              {collapsed[day] ? "+" : "-"}
            </button>
            {day}
          </div>
          {!collapsed[day] && (
            <div className="grid3 mt20">
              {data
                .filter(schedule => schedule.day_of_week === day)
                .sort(sortByTime)
                .map((schedule, idx) => (
                  <TableCard
                    key={idx}
                    time={schedule.time}
                    name={schedule.name}
                    address={schedule.address}
                    color={schedule.metro_color}
                  />
                ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
