import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SeasonSelector from './SeasonSelector';
import FilialSelector from './FilialSelector';
import AnalyticsTable from './AnalyticsTable';
import ProfitChart from './ProfitChart';
import { API_BASE_URL } from './Api';

const Analytics = () => {
  const [selectedSeason, setSelectedSeason] = useState('');
  const [selectedFilial, setSelectedFilial] = useState('');
  const [data, setData] = useState([]);

  useEffect(() => {
    if (selectedSeason) {
      fetchData();
    }
  }, [selectedSeason, selectedFilial]);

  const fetchData = () => {
    const [startYear, endYear] = selectedSeason.split('-');
    const months = [...Array(12).keys()].map(i => `${startYear}-${(i + 1).toString().padStart(2, '0')}`);

    Promise.all(months.map(month => axios.get(`${API_BASE_URL}/dnevnik/${month}`)))
      .then(responses => {
        const allData = responses.map(response => response.data);
        setData(allData);
      })
      .catch(error => {
        console.error('Ошибка при получении данных аналитики:', error);
      });
  };

  return (
    <div className='analytics'>
      <SeasonSelector selectedSeason={selectedSeason} setSelectedSeason={setSelectedSeason} />
      <FilialSelector selectedFilial={selectedFilial} setSelectedFilial={setSelectedFilial} />
      <AnalyticsTable data={data} />
      <ProfitChart data={data} />
    </div>
  );
};

export default Analytics;
