import React, { useState, useEffect } from 'react';
import axios from 'axios';
import YearSelector from './YearSelector';
import FilialSelector from './FilialSelector';
import AnalyticsTable from './AnalyticsTable';
import ProfitChart from './ProfitChart';
import { API_BASE_URL } from './Api';

const Analytics = () => {
  const [selectedYear, setSelectedYear] = useState('');
  const [selectedFilial, setSelectedFilial] = useState('');
  const [data, setData] = useState([]);

  useEffect(() => {
    if (selectedYear) {
      fetchData();
    }
  }, [selectedYear, selectedFilial]);

  const fetchData = async () => {
    const months = [...Array(12).keys()].map(i => (i + 1).toString().padStart(2, '0'));

    try {
      const responses = await Promise.all(months.map(month => 
        axios.get(`${API_BASE_URL}/dnevnik/${selectedYear}/${month}`)
      ));

      const allData = responses.map(response => response.data).flat();
      console.log("All Data:", allData);  // Логирование всех данных
      const groupedData = groupDataByMonth(allData);
      console.log("Grouped Data:", groupedData);  // Логирование сгруппированных данных
      setData(groupedData);
    } catch (error) {
      console.error('Ошибка при получении данных аналитики:', error);
    }
  };

  const groupDataByMonth = (data) => {
    const months = [
      'Январь', 'Февраль', 'Март', 'Апрель', 'Май', 'Июнь', 
      'Июль', 'Август', 'Сентябрь', 'Октябрь', 'Ноябрь', 'Декабрь'
    ];

    const grouped = data.reduce((acc, record) => {
      const monthIndex = new Date(record.date).getMonth();
      const monthName = months[monthIndex];
      if (!acc[monthName]) {
        acc[monthName] = [];
      }
      acc[monthName].push(record);
      return acc;
    }, {});

    return Object.keys(grouped).map(month => ({
      month,
      records: grouped[month]
    }));
  };

  return (
    <div className='analytics'>
      <YearSelector selectedYear={selectedYear} setSelectedYear={setSelectedYear} />
      <FilialSelector selectedFilial={selectedFilial} setSelectedFilial={setSelectedFilial} />
      <AnalyticsTable data={data} />
      <ProfitChart data={data} />
    </div>
  );
};

export default Analytics;
