import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Card from './Card';
import Modal from './Modal';

const PayInfo = () => {
    const [invoices, setInvoices] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        const fetchInvoices = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/payinfo');
                setInvoices(response.data.items);
            } catch (error) {
                console.error('Error fetching invoices:', error);
            }
        };

        fetchInvoices();
    }, []);

    const handleSave = async (paymentData) => {
        try {
            const response = await axios.post('http://localhost:3001/api/payinfo', paymentData, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            setInvoices([...invoices, response.data]);
        } catch (error) {
            console.error('Error creating payment:', error);
        }
    };

    return (
        <div>
            <h2>Список счетов</h2>
            <button onClick={() => setIsModalOpen(true)}>Добавить новый платеж</button>
            <div>
                {invoices.map(invoice => (
                    <Card key={invoice.id} invoice={invoice} />
                ))}
            </div>
            <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} onSave={handleSave} />
        </div>
    );
};

export default PayInfo;
